mapper.select:
SELECT s1.id, s1.title, CONCAT( s2.category_name, "/", s3.category_name ) AS categoryName, s4.brand_name as brandName FROM lm_spu s1 LEFT JOIN lm_category s2 ON s1.cid1 = s2.id LEFT JOIN lm_category s3 ON s1.cid2 = s3.id LEFT JOIN lm_brand s4 ON s1.brand_id = s4.id WHERE 1=1
and s1.shelf_flag=#{req.shelfFlag}
and s4.brand_name like CONCAT('%',#{req.keyWords},'%')

SELECT count(*) FROM lm_spu s1 LEFT JOIN lm_category s2 ON s1.cid1 = s2.id LEFT JOIN lm_category s3 ON s1.cid2 = s3.id LEFT JOIN lm_brand s4 ON s1.brand_id = s4.id WHERE 1=1
and s1.shelf_flag=#{req.shelfFlag}
and s4.brand_name like CONCAT('%',#{req.keyWords},'%')
